import React, {useState} from "react";
import SnackOrBoozeApi from "./Api";
import { Card, CardBody, CardTitle} from "reactstrap";

function FoodForm({addFood, foodType}){

    const initialState = {
        foodType:"",
        name:"",
        description:"",
        recipe:"",
        serve:""
    }

    const [formData, setFormData] = useState(initialState)

    const handleChange = (e) => {
        const target = e.target;
        const name = target.name;
        const value = target.type === 'select' ? target.selected : target.value;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (handleValidation() === true){
            addFood({...formData});
            SnackOrBoozeApi.createFood(formData, formData.foodType);
            setFormData(initialState);
        }
        return;
    }

    const handleValidation = () => {
        for (let key in formData){
            if (formData[key].length === 0){
                alert(`${key} was not filled!`)
                return false;
            }
        }
        return true;
    }

    return (
        <Card>
            <CardBody>
                <CardTitle>
                    Create A New Food
                </CardTitle>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="foodType">Select Your Food Type</label>
                    <input 
                        type="radio" 
                        id="drinks"
                        name="foodType" 
                        value={formData.foodType = "drinks"} 
                        onChange={handleChange} />
                    <label htmlFor="drinks">Drinks</label>
                    <input 
                        type="radio" 
                        id="snacks"
                        name="foodType"
                        value={formData.foodType = "snacks"} 
                        onChange={handleChange} />
                    <label htmlFor="snacks">Snacks</label>
                    <label htmlFor="name">Name</label>
                    <input 
                        type="text" 
                        id="name"
                        name="name" 
                        placeholder="Food Name"
                        value={formData.name} 
                        onChange={handleChange} />
                    <label htmlFor="description">Description</label>
                    <input 
                        type="text" 
                        id="description"
                        name="description" 
                        placeholder="Food Description"
                        value={formData.description} 
                        onChange={handleChange} />
                    <label htmlFor="recipe">Recipe</label>
                    <input 
                        type="text" 
                        id="recipe"
                        name="recipe" 
                        placeholder="Recipe" 
                        value={formData.recipe} 
                        onChange={handleChange} />
                    <label htmlFor="serve">Serve</label>
                    <input
                        type="text" 
                        id="serve"
                        name="serve" 
                        placeholder="Serve" 
                        value={formData.serve} 
                        onChange={handleChange} />                            
                            
                        <button>Create</button>
                </form>                
            </CardBody>
        </Card>
        
    )

}

export default FoodForm;
