import React, {useState} from "react";
import SnackOrBoozeApi from "./Api";
import { Card, CardBody, CardTitle, Button} from "reactstrap";

function FoodForm({addFood, foodType}){

    const initialState = {
        foodType:"",
        name:"",
        description:"",
        recipe:"",
        serve:""
    }

    const [formData, setFormData] = useState(initialState)
    const handleFoodType = (e) => {
        const value = e.target;
        setFormData(data => ({
            ...data,
            foodType: value
        }))
    }

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (handleValidation() === true){
            addFood({...formData});
            SnackOrBoozeApi.createFood(formData, formData.foodType);
            setFormData(initialState);
        }
        return;
    }

    const handleValidation = () => {
        for (let key in formData){
            if (formData[key].length === 0){
                alert(`${key} was not filled!`)
                return false;
            }
        }
        return true;
    }

    return (
        <Card>
            <CardBody>
                <CardTitle>
                    Create A New Food
                </CardTitle>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="foodType">Select Your Food Type</label>
                    <select name="foodType" value={formData.foodType} onChange={handleChange}>
                        <option>Select Food Type</option>
                        <option value="drinks">Drinks</option>
                        <option value="snacks">Snacks</option>
                    </select>
                    <label htmlFor="name">Name</label>
                    <input 
                        type="text" 
                        id="name"
                        name="name" 
                        placeholder="Food Name"
                        value={formData.name} 
                        onChange={handleChange} />
                    <label htmlFor="description">Description</label>
                    <input 
                        type="text" 
                        id="description"
                        name="description" 
                        placeholder="Food Description"
                        value={formData.description} 
                        onChange={handleChange} />
                    <label htmlFor="recipe">Recipe</label>
                    <input 
                        type="text" 
                        id="recipe"
                        name="recipe" 
                        placeholder="Recipe" 
                        value={formData.recipe} 
                        onChange={handleChange} />
                    <label htmlFor="serve">Serve</label>
                    <input
                        type="text" 
                        id="serve"
                        name="serve" 
                        placeholder="Serve" 
                        value={formData.serve} 
                        onChange={handleChange} />                            
                            
                        <Button>Create</Button>
                </form>                
            </CardBody>
        </Card>
        
    )

}

export default FoodForm;
