import React, {useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";
import "./App.css";
import Home from "./Home";
import SnackOrBoozeApi from "./Api";
import NavBar from "./NavBar";
import { Route, Switch } from "react-router-dom";
import Menu from "./FoodMenu";
import Food from "./FoodItem";
import Form from "./FoodForm";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [snacks, setSnacks] = useState([]);
  const [drinks, setDrinks] = useState([]);
  const [newFood, setNewFood] = useState([]);

  const addFood = (newFood) => {
    setNewFood(newFood)
};

  useEffect(() => {
    async function getSnacks() {
      let snacks = await SnackOrBoozeApi.getSnacks();
      setSnacks(snacks);
      setIsLoading(false);
    }
    getSnacks();
  }, []);

  useEffect(() => {
    async function getDrinks() {
      let drinks = await SnackOrBoozeApi.getDrinks();
      setDrinks(drinks);
      setIsLoading(false);
    }
    getDrinks();
  }, []);

  if (isLoading) {
    return <p>Loading &hellip;</p>;
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />
        <main>
          <Switch>
            <Route exact path="/">
              <Home snacks={snacks} />
            </Route>
            <Route exact path="/snacks">
              <Menu food={snacks} foodType="snacks" title="Snacks" />
            </Route>
            <Route exact path="/drinks">
              <Menu food={drinks} foodType="drinks" title="Drink" />
            </Route>
            <Route path="/snacks/:id">
              <Food items={snacks} foodType="snacks" cantFind="/snacks" />
            </Route>
            <Route path="/drinks/:id">
              <Food items={drinks} foodType="drinks" cantFind="/drinks" />
            </Route>
            <Route path="/add-new-food">
              <Form addFood={addFood} cantFind="/" />
            </Route>
            <Route>
              <p>Hmmm. I can't seem to find what you want.</p>
            </Route>
          </Switch>
        </main>
      </BrowserRouter>
    </div>
  );
}

export default App;
